/*----------------------------------------------------
/* Add random post content wrapper when document ready
/*--------------------------------------------------*/
jQuery(document).ready(function($) {

    jQuery("body").prepend("<div class='modal fade random-overlay overlay' role='dialog'><a class='button button--close'  data-dismiss='modal'>Ã—</a><div class='overlay-dialog overlay-dialog--animate'><div id='resultarea' class='resultarea'></div><a class='button--close--bottom'  data-dismiss='modal'>Close</a></div></div>");
    
    var scrollDes = 'html,body';  
    /*Opera does a strange thing if we use 'html' and 'body' together so my solution is to do the UA sniffing thing*/
    if(navigator.userAgent.match(/opera/i)){
        scrollDes = 'html';
    }
});

/*----------------------------------------------------
/* Random button
/*---------------------------------------------------*/
jQuery( document ).on( 'click', '.random-button', function(e) {

    e.preventDefault();

    var $this = jQuery(this),
        nonce = jQuery(this).attr("data-nonce"),
        cat = jQuery(this).attr('data-cat'),
        nocat = jQuery(this).attr('data-nocat');

    if (jQuery('.resultarea').length) {
        jQuery('.resultarea').empty();
    }

    if (jQuery('.overlay-dialog').length) {
        jQuery('.overlay-dialog').addClass( 'loading' );
    }

    jQuery.ajax({
        url : randomwpstuff.ajax_url,
        type : 'post',
        data : {
            action : 'pt_randomwpstuff_function',
            nonce : nonce,
            cat : cat,
            nocat : nocat
        },
        success : function( response ) {
            jQuery('.overlay-dialog').removeClass( 'loading' );
            jQuery( '.resultarea' ).html( response );
        }
    });
     
    return false;
})